mapper.insert:
SELECT CONCAT('A', LPAD(NVL(MAX(SUBSTR(ASSET,2,5)),0)+1,5,'0')) ASSET
     , NVL(MAX(SORT_NO),0)+1 SORT_NO
  FROM info_asset

INSERT INTO info_asset
		(
			ASSET
		  , SORT_NO
		  , KR_NM
		  , EN_NM
		  , ASSET_KIND
		  , OWNER
		  , FIRM
		  , FROM_DT
		  , TO_DT
		  , CHRG_YN
		  , CHRG_COND_AMT
		  , CHRG_ASSET
		  , CHRG_AMT
		  , PAY_ASSET
		  , PAY_DAY
		  , TOT_DAY
		)
VALUES
		(
			#{ASSET}
		  , #{SORT_NO}
		  , #{KR_NM}
		  , #{EN_NM}
		  , #{ASSET_KIND}
		  , #{OWNER}
		  , #{FIRM}
		  , #{FROM_DT}
		  , #{TO_DT}
		  , #{CHRG_YN}
		  , #{CHRG_COND_AMT}
		  , #{CHRG_ASSET}
		  , #{CHRG_AMT}
		  , #{PAY_ASSET}
		  , #{PAY_DAY}
		  , #{TOT_DAY}		
		)
