mapper.insert:
INSERT INTO mast_asset
		(
			WK_DT
		  , ASSET
		  , BEF_AMT
		  , INC_AMT
		  , DEC_AMT
		  , AMT
		  , BEF_DT
		)
VALUES
		(
			#{WK_DT}
		  , #{ASSET}
		  , #{BEF_AMT}
		  , #{INC_AMT}
		  , #{DEC_AMT}
		  , #{AMT}
		  , #{BEF_DT}		
		)

mapper.update:
UPDATE mast_asset
   SET INC_AMT = #{INC_AMT}
     , DEC_AMT = #{DEC_AMT}
     , AMT = #{AMT}
 WHERE WK_DT = #{WK_DT}
   AND ASSET = #{ASSET}

UPDATE mast_asset
   SET BEF_DT = #{BEF_DT}
 WHERE WK_DT = #{WK_DT}
   AND ASSET = #{ASSET}

UPDATE mast_asset
   SET BEF_AMT 	= BEF_AMT 	- #{AMT}
     , AMT 		= AMT 		- #{AMT}
 WHERE WK_DT > #{WK_DT}
   AND ASSET = #{ASSET}

UPDATE mast_asset
   SET BEF_AMT 	= BEF_AMT 	+ #{AMT}
     , AMT 		= AMT 		+ #{AMT}
 WHERE WK_DT > #{WK_DT}
   AND ASSET = #{ASSET}
